definition.script:
pipeline {
  agent any

  stages {
    stage('Checkout') {
      steps {
        git branch: 'main', credentialsId: 'githubid', url: 'https://github.com/sunilkumarksk/simplejavaapp.git'
      }
    }
    stage('sonarqube') {
      steps {
        withSonarQubeEnv('sonarqube-7.6') {
          sh 'mvn sonar:sonar'
        }
      }
    }
    stage("Quality Gate") {
      steps {
        timeout(time: 5, unit: 'MINUTES') {
          waitForQualityGate abortPipeline: true
        }
      }
    }
    stage("Build") {
      steps {
        sh 'mvn clean install'
      }
    }
    stage("App Deploy") {
      steps {
          sh 'sudo chmod -R 777 /home/sunilkumark1994/sunil/tomcat/apache-tomcat-8.5.78/webapps'
        sh 'sudo cp ./target/java-hello-world.war /home/sunilkumark1994/sunil/tomcat/apache-tomcat-8.5.78/webapps/'
      }
    }

  }
}
